var campoFiltro=document.querySelector("#filtrar-tabela");campoFiltro.addEventListener("input",(function(){console.log(this.value);var e=document.querySelectorAll(".evento");if(this.value.length>0)for(var t=0;t<e.length;t++){var i=(l=e[t]).querySelector(".info-nome").textContent;new RegExp(this.value,"i").test(i)?l.classList.remove("invisivel"):l.classList.add("invisivel")}else for(t=0;t<e.length;t++){var l;(l=e[t]).classList.remove("invisivel")}}));